program addNum
var
	num1, num2, total : integer;
begin
	writeln('Enter two numbers');
	readln(num1);
	readln(num2);
	total := num1 + num2;
	writeln('Total = ', total);
end.

Var N : integer;
begin
	readln(N);
	If N > 0 then
		writeln(N);
	End.

Var N1, N2, Large : integer;
begin
	readln(N1, N2);
	If N1 > N2 then
		Large := N1
	Else
		Large := N2;
	writeln(Large);
End.

Var M: integer; Grade: char;
begin
	readln(M);
	If M >= 75 then Grade := 'A'
	Else If M >= 65 then Grade := 'B'
	Else If M >= 50 then Grade := 'C'
	Else If M >= 35 then Grade := 'S'
	Else Grade := 'F';
	writeln(Grade);
End.

var Marks : integer; Grade: char;
begin
	readln(Marks);
	Case Marks of
		0..34 : Grade := 'W';
		35..49 : Grade := 'W';
		50..64 : Grade := 'C';
		65..74 : Grade := 'A'; // Note: Text says 65-74 Result='A' but implies different grading logic
		75..100 : Grade := 'A';
		Else writeln('Invalid');
	End;
	writeln(Grade);
End.

Var count : integer;
begin
	for count := 1 to 10 do
		writeln(count);
end.

Var count : integer;
begin
	for count := 10 downto 1 do
		writeln(count);
end.

Var number : integer;
begin
	number := 1;
	while number <= 10 do
	begin
		writeln(number);
		number := number + 1;
	end;
end.

Var count : integer;
begin
	count := 0;
	Repeat
		writeln('Pascal');
		count := count + 1;
	Until count > 5;
end.

var num:integer; cho:char;
begin
	readln(cho);
	if cho = 'A' then
	begin
		for num := 1 to 6 do
			writeln(num);
	end;
	if cho = 'D' then
	begin
		for num := 6 downto 1 do
			writeln(num);
	end;
end.

var count, num, rem : integer;
begin
	for count := 1 to 10 do
	begin
		readln(num);
		rem := num mod 2;
		if rem = 0 then writeln('Even')
		else writeln('Odd');
	end;
end.

program ictMarks(input,output);
var marks : array[0..34] of integer;
i,tot,max : integer;
avg : real;
begin
	tot := 0;
	for i := 0 to 34 do
	begin
		writeln('Enter Marks');
		readln(marks[i]);
		tot := tot + marks[i];
	end;
	avg := tot/35;
	max := marks[0];
	for i := 1 to 34 do
	begin
		if marks[i] > max then max := marks[i];
	end;
	writeln('Maximum marks = ', max);
	writeln('Average marks = ',avg);
end.

program circleCalc;
var radius:real;
procedure inputRadius;
begin
	readln(radius);
end;
procedure processArea;
var area:real;
begin
	area := 3.14 * radius * radius;
	writeln(area);
end;
procedure processCircumference;
var circum:real;
begin
	circum := 2 * 3.14 * radius;
	writeln(circum);
end;
begin
	inputRadius;
	processArea;
	processCircumference;
end.

program circleCalcFunc;
var radius:real;
function processArea : real;
var area:real;
begin
	area := 3.14 * radius * radius;
	processArea := area;
end;
function processCircumference : real;
var circum:real;
begin
	circum := 2 * 3.14 * radius;
	processCircumference := circum;
end;
begin
	readln(radius);
	writeln(processArea);
	writeln(processCircumference);
end.
